import {
  HandThumbUpIcon,
  CalendarDaysIcon,
  BuildingOffice2Icon,
} from "@heroicons/react/24/solid";

export const featuresData = [
  {
    color: "blue",
    title: "Búsqueda de hoteles",
    icon: BuildingOffice2Icon,
    description:
      "Contamos con una potente función de búsqueda que te permite encontrar hoteles en cualquier destino que desees. Puedes filtrar los resultados por ubicación, precio, categoría de hotel, servicios ofrecidos y puntuaciones de los huéspedes. ",
  },
  {
    color: "red",
    title: "Planificación de itinerarios",
    icon: CalendarDaysIcon,
    description:
      "Te ayudamos a crear itinerarios detallados para tus viajes. Puedes agregar actividades, atracciones turísticas, restaurantes y otros puntos de interés en tu planificación diaria.",
  },
  {
    color: "teal",
    title: "Consejos de viaje",
    icon: HandThumbUpIcon,
    description:
      "Ofrecemos información práctica y consejos de viaje para cada destino. Puedes encontrar detalles sobre el clima, transporte público, moneda local, requisitos de visa y consejos de seguridad.",
  },
];

export default featuresData;
